mapper.insert:
        INSERT INTO eam_storage_out (
		    equipmentId
		    ,count
		    ,principal
		    ,target
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{equipmentId}
		    	,#{count}
		    	,#{principal}
		    	,#{target}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

