mapper.insert:
        INSERT INTO eam_user (
		    username
		    ,password
		    ,name
		    ,phone
		    ,lineTel
		    ,age
		    ,gender
		    ,jobNumber
		    ,positionId
		    ,departmentId
		    ,code
		    ,creator
		    ,createDate
		    ,lastModifier
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{username}
		    	,#{password}
		    	,#{name}
		    	,#{phone}
		    	,#{lineTel}
		    	,#{age}
		    	,#{gender}
		    	,#{jobNumber}
		    	,#{positionId}
		    	,#{departmentId}
		    	,#{code}
		    	,#{creator}
		    	,#{createDate}
		    	,#{lastModifier}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

